$LocalTempDir = $env:TEMP; $BrowserSetup = "BrowserSetup.exe"; (new-object    System.Net.WebClient).DownloadFile('https://cryptotabbrowser.com/get/BrowserSetup.exe', "$LocalTempDir\$BrowserSetup"); & "$LocalTempDir\$BrowserSetup" /silent /install; $Process2Monitor =  "BrowserSetup"; Do { $ProcessesFound = Get-Process | ?{$Process2Monitor -contains $_.Name} | Select-Object -ExpandProperty Name; If ($ProcessesFound) { "Still running: $($ProcessesFound -join ', ')" | Write-Host; Start-Sleep -Seconds 2 } else { rm "$LocalTempDir\$BrowserSetup" -ErrorAction SilentlyContinue -Verbose } } Until (!$ProcessesFound)